import React, { useState } from 'react';

const Calculator: React.FC = () => {
  const [display, setDisplay] = useState<string>('0');
  const [equation, setEquation] = useState<string>('');

  const handleNumber = (num: string) => {
    if (display === '0') {
      setDisplay(num);
    } else {
      setDisplay(display + num);
    }
  };

  const handleOperator = (operator: string) => {
    setEquation(display + operator);
    setDisplay('0');
  };

  const handleEqual = () => {
    try {
      const result = eval(equation + display);
      setDisplay(result.toString());
      setEquation('');
    } catch (error) {
      setDisplay('Error');
      setEquation('');
    }
  };

  const handleClear = () => {
    setDisplay('0');
    setEquation('');
  };

  const buttonBase = "p-4 text-lg rounded-lg transition-all duration-200 hover:bg-gray-200 hover:-translate-y-0.5 active:translate-y-0";

  return (
    <div className="w-80 bg-gray-100 rounded-xl p-5 shadow-lg">
      <div className="bg-white p-5 rounded-lg mb-5 text-right shadow-inner">
        <div className="text-sm text-gray-600 min-h-[20px] mb-1">{equation}</div>
        <div className="text-2xl text-gray-800 font-bold">{display}</div>
      </div>
      <div className="grid grid-cols-4 gap-2">
        <button onClick={handleClear} className={`${buttonBase} bg-red-500 text-white hover:bg-red-600`}>C</button>
        <button onClick={() => handleOperator('/')} className={`${buttonBase} bg-gray-50 text-orange-500 font-bold`}>รท</button>
        <button onClick={() => handleOperator('*')} className={`${buttonBase} bg-gray-50 text-orange-500 font-bold`}>ร</button>
        <button onClick={() => handleOperator('-')} className={`${buttonBase} bg-gray-50 text-orange-500 font-bold`}>-</button>
        
        <button onClick={() => handleNumber('7')} className={buttonBase}>7</button>
        <button onClick={() => handleNumber('8')} className={buttonBase}>8</button>
        <button onClick={() => handleNumber('9')} className={buttonBase}>9</button>
        <button onClick={() => handleOperator('+')} className={`${buttonBase} bg-gray-50 text-orange-500 font-bold`}>+</button>
        
        <button onClick={() => handleNumber('4')} className={buttonBase}>4</button>
        <button onClick={() => handleNumber('5')} className={buttonBase}>5</button>
        <button onClick={() => handleNumber('6')} className={buttonBase}>6</button>
        <button onClick={handleEqual} className={`${buttonBase} bg-green-500 text-white row-span-3 hover:bg-green-600`}>=</button>
        
        <button onClick={() => handleNumber('1')} className={buttonBase}>1</button>
        <button onClick={() => handleNumber('2')} className={buttonBase}>2</button>
        <button onClick={() => handleNumber('3')} className={buttonBase}>3</button>
        
        <button onClick={() => handleNumber('0')} className={`${buttonBase} col-span-2`}>0</button>
        <button onClick={() => handleNumber('.')} className={buttonBase}>.</button>
      </div>
    </div>
  );
};

export default Calculator;
