import React, { useState } from 'react';

const Calculator: React.FC = () => {
  const [display, setDisplay] = useState<string>('0');
  const [equation, setEquation] = useState<string>('');

  const handleNumber = (num: string) => {
    if (display === '0') {
      setDisplay(num);
    } else {
      setDisplay(display + num);
    }
  };

  const handleOperator = (operator: string) => {
    setEquation(display + operator);
    setDisplay('0');
  };

  const handleEqual = () => {
    try {
      const result = eval(equation + display);
      setDisplay(result.toString());
      setEquation('');
    } catch (error) {
      setDisplay('Error');
      setEquation('');
    }
  };

  const handleClear = () => {
    setDisplay('0');
    setEquation('');
  };

  const buttonBase = "w-full h-14 text-xl font-medium rounded-lg transition-all duration-200 active:scale-95 hover:brightness-95";
  const numberButton = `${buttonBase} bg-white text-gray-800 shadow-sm`;
  const operatorButton = `${buttonBase} bg-gray-100 text-orange-500 font-bold`;

  return (
    <div className="w-80 bg-gray-50 rounded-2xl p-6 shadow-xl">
      <div className="bg-white p-4 rounded-xl mb-4 shadow-inner">
        <div className="text-sm text-gray-500 min-h-[20px] mb-1 text-right">{equation}</div>
        <div className="text-3xl text-gray-800 font-bold text-right overflow-hidden">{display}</div>
      </div>
      <div className="grid grid-cols-4 gap-3">
        <button onClick={handleClear} className={`${buttonBase} bg-red-500 text-white hover:bg-red-600`}>C</button>
        <button onClick={() => handleOperator('/')} className={operatorButton}>รท</button>
        <button onClick={() => handleOperator('*')} className={operatorButton}>ร</button>
        <button onClick={() => handleOperator('-')} className={operatorButton}>-</button>
        
        <button onClick={() => handleNumber('7')} className={numberButton}>7</button>
        <button onClick={() => handleNumber('8')} className={numberButton}>8</button>
        <button onClick={() => handleNumber('9')} className={numberButton}>9</button>
        <button onClick={() => handleOperator('+')} className={operatorButton}>+</button>
        
        <button onClick={() => handleNumber('4')} className={numberButton}>4</button>
        <button onClick={() => handleNumber('5')} className={numberButton}>5</button>
        <button onClick={() => handleNumber('6')} className={numberButton}>6</button>
        <button onClick={handleEqual} className={`${buttonBase} bg-green-500 text-white hover:bg-green-600 row-span-3`}>=</button>
        
        <button onClick={() => handleNumber('1')} className={numberButton}>1</button>
        <button onClick={() => handleNumber('2')} className={numberButton}>2</button>
        <button onClick={() => handleNumber('3')} className={numberButton}>3</button>
        
        <button onClick={() => handleNumber('0')} className={`${numberButton} col-span-2`}>0</button>
        <button onClick={() => handleNumber('.')} className={numberButton}>.</button>
      </div>
    </div>
  );
};

export default Calculator;
